import React, { useState, useEffect } from "react";

import "./style.scss";

import preview from "../../img/hero.png";

const words = ["Слово1", "Слово2", "Слово3"];

const Hero = () => {
    const [currentWordIndex, setCurrentWordIndex] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentWordIndex((prevIndex) => (prevIndex + 1) % words.length);
        }, 10000);

        return () => clearInterval(interval);
    }, []);

    return (
        <section className="hero">
            <div className="container">
                <div className="hero__inner">
                    {/* <h1 className="hero__title">$wen pump?</h1> */}
                    <h1 className="hero__title">$wen {words[currentWordIndex]}</h1>
                    <img src={preview} alt="preview" className="hero__img" />
                </div>
            </div>
        </section>
    );
};

export default Hero;
