import { useState } from "react";
import Web3 from "web3";

import "./style.scss";

import logo from "../../img/logo.png";

const Header = () => {
    const [web3, setWeb3] = useState(null);
    const [account, setAccount] = useState(null);
    const [balance, setBalance] = useState(null);

    const connectWallet = async () => {
        if (window.ethereum) {
            try {
                const web3Instance = new Web3(window.ethereum);
                await window.ethereum.enable(); // Request user permission to connect
                const accounts = await web3Instance.eth.getAccounts();
                const selectedAccount = accounts[0];
                setWeb3(web3Instance);
                setAccount(selectedAccount);
                updateBalance(selectedAccount, web3Instance);
            } catch (error) {
                console.error("Error connecting wallet:", error);
            }
        } else {
            console.error(
                "No Ethereum provider found. Please install MetaMask or another wallet extension."
            );
        }
    };
    const contract = new web3Instance.eth.Contract(wenContractABI, wenContractAddress);
    const wen = contract.methods.balanceOf(selectedAccount).call();
    

    const updateBalance = async (selectedAccount, web3Instance) => {
        const weiBalance = await web3Instance.eth.getBalance(selectedAccount);
        const ethBalance = web3Instance.utils.fromWei(weiBalance, "ether");
        setBalance(ethBalance);
    };

    return (
        <header className="header">
            <div className="container">
                <div className="header__inner">
                    <div className="logo">
                        <img className="logo__img" src={logo} alt="logo" />
                        <p className="logo__text">$wen$base</p>
                    </div>
                    <div className="wallet">
                        {web3 === null ? (
                            <a
                                href="#!"
                                className="wallet__text"
                                onClick={connectWallet}
                            >
                                Connect wallet
                            </a>
                        ) : (
                            <>
                                <p className="wallet__text">Wallet connected</p>
                                <div className="wallet__data">
                                    <span className="wallet__wen">
                                        121 $WEN
                                    </span>
                                    <span className="wallet__eth">
                                        {parseFloat(balance).toFixed(2)} $ETH
                                    </span>
                                </div>
                            </>
                        )}
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Header;
