import { useState } from "react";
import Web3 from "web3";

import "./style.scss";

import logo from "../../img/logo.png";

const Header = () => {
    const [web3, setWeb3] = useState(null);
    const [balance, setBalance] = useState(null);
    const [wenbalance, setWenBalance] = useState(null);

    const connectWallet = async () => {
        if (window.ethereum) {
            const web3Instance = new Web3(window.ethereum);
            setWeb3(web3Instance);
            await window.ethereum.enable();
            const accounts = await web3Instance.eth.getAccounts();
            const selectedAccount = accounts[0];
            const weiBalance = await web3Instance.eth.getBalance(
                selectedAccount
            );
            const ethBalance = web3Instance.utils.fromWei(weiBalance, "ether");
            setBalance(ethBalance);
            const contract = new web3Instance.eth.Contract(
                wenContractABI,
                wenContractAddress
            );
            const wen = await contract.methods
                .balanceOf(selectedAccount)
                .call();
            setWenBalance(wen);
        } else {
            alert("MetaMask isn't detected");
        }
    };

    return (
        <header className="header">
            <div className="container">
                <div className="header__inner">
                    <div className="logo">
                        <img className="logo__img" src={logo} alt="logo" />
                        <p className="logo__text">$wen$base</p>
                    </div>
                    <div className="wallet">
                        {web3 === null ? (
                            <a
                                href="#!"
                                className="wallet__text"
                                onClick={connectWallet}
                            >
                                Connect wallet
                            </a>
                        ) : (
                            <>
                                <p className="wallet__text">Wallet connected</p>
                                <div className="wallet__data">
                                    <span className="wallet__wen">
                                        121 $WEN
                                    </span>
                                    <span className="wallet__eth">
                                        {parseFloat(balance).toFixed(2)} $ETH
                                    </span>
                                </div>
                            </>
                        )}
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Header;
